mapper.select:

select *
from (select rownum rn, a.*
	  from (select p.* FROM post p inner join board b on(p.bd_num = b.bd_num) WHERE b.BD_TYPE in (3) order by p_num desc) 
	  a) where rn >= #{start} and rn <= #{end}

mapper.update:
UPDATE post SET
      p_name = #{p_name},
      p_content = #{p_content},
      m_id = #{m_id}
  WHERE p_num = #{p_num}

mapper.select:


		select * 
		from (select rownum rn, a.*  
 			  from (select * from post where bd_num=#{bd_num} order by p_num) a) where rn >= #{start} and rn <= #{end}


select *
from (select rownum rn, a.*
	  from (select * from board order by bd_num desc) a) where rn >= #{start} and rn <= #{end}
