mapper.insert:
INSERT INTO mail(
		ml_num, ml_email, mlb_num, ml_title, ml_content, 
		ml_regdate, ml_rcvdate, ml_type
)
VALUES(
		#{ml_num}, #{ml_email}, #{mlb_num}, #{ml_title}, #{ml_content}, 
		#{ml_regdate}, #{ml_rcvdate, jdbcType = DATE}, #{ml_type}
)

INSERT INTO mail_file(
	ml_num, dv_id
)
VALUES (
	#{ml_num}, #{dv_id}
)

INSERT INTO mail_to(
	ml_num, ml_email
)
VALUES (
	#{ml_num}, #{ml_email}
)

mapper.select:

SELECT *
FROM (SELECT ROWNUM rn, a.* FROM (
	SELECT ml.* FROM mail ml, mail_to mlt WHERE ml.ml_num = mlt.ml_num AND mlt.ml_email = #{ml_email} AND ml.ml_type = #{ml_type}
	ORDER BY ml.ml_regdate desc			  		  
) a) WHERE rn >= #{rn_start} and rn <= #{rn_end}
